   }
    return(
        <div>
            <h1>
               biçim bileşeni
            </h1>
            <form>
                <button onClick={()=>alert("içleniyor")}>
                çizgi içi
                </button>
                <button onClick= {handle}>ok</button>
                <button onClick={()=>deal('Girdi')}>değiştirgen</button>

            </form>
            
        </div>
    )
}
