import {useState} from 'react'

export default function ObjectComponent(){
   const initialProduct={id:301,name:"cep telefonu",price:2350.0}
   const [product,setProduct] = useState(initialProduct);

   const nameChanged=(event)=>{
    setProduct({
        id:301,
    name : event.target.value,
    price : product.price
    })
   }


    return(
        <div>
            <h1>
              nesne bileşeni
            </h1>
            {product.id} - {product.name} - {product.price}
            <form>
            özdeşlik : {product.id}<br/>
            ad : <input type="text" value={product.name}/><br/>
            eder : <input type="text" value={product.name} onChange={nameChanged}/><br/><br/>
            </form>
        </div>
    )
}
