const initialState = {
    quantity:0


}

const holderReducer = (state=initialState,action) =>{

    switch(action){
        case 'quantity/increment':
            return {
                quantity : state.quantity + 1
                
            }
        default : 
            return state;
    }

}

export default holderReducer();
