        console.log("Etki kullan")
        fetch(urlGet)          //axious -- fetch yerine  ?? 
        .then(data=>data.json()) 
        .then(product=>
            console.log(product))
    },[urlGet])
    return(
        <>
            Git - getir dizelge 
        </>
    )
}
